import React from "react";
import Buld from "../../Assets/Icons/Bulb";
import File from "../../Assets/Icons/File";
import Quit from "../../Assets/Icons/Quit";
import Card from "../../Components/Cards/index";
import { useState } from "react";
import "./main.css";

const Main = () => {
  const [active, setActive] = useState(true);
  const handleQuict = () => {
    setActive(!active);
  };
  return (
    <div className=" flex flex-col md:flex-row   justify-start mt-8 px-4">
      <div className="w-[20rem]"></div>
      <div className="flex flex-col gap-[5rem] ">
        <div>
          <div className="flex flex-col gap-4">
            <div className="flex flex-col sm:flex-row gap-2 sm;gap-0 items-center w-full justify-between">
              <h1 className=" font-semibold text-[1.46rem]">Opportunities</h1>
              <button className=" p-[0.6rem] bg-yellow-400 hover:bg-[#F5D780] rounded-full font-medium flex gap-2 px-[1rem]">
                <span>
                  <File />
                </span>{" "}
                Create Proposel
              </button>
            </div>
            <nav>
              <div className=" absolute flex gap-3 font-semibold border-[3px]  border-transparent rounded-sm border-solid   !border-b-SecondaryWhite  ">
                <button className=" relative top-[.18rem]  pb-2 border-[3px]  border-transparent  border-solid border-b-yellow-300 ">
                  Discover
                </button>
                <button className="cool- relative top-[.18rem] after:block after:w-0 after:h-[2px] after:bg-yellow-300 after:transition-width  hover:after:w-[5rem]  ">
                  Applied
                </button>
              </div>
            </nav>
          </div>
        </div>
        <div className="flex flex-col gap-2">
          {active ? (
            <div className="  bg-SecondaryWhite  md:w-[45rem] gap-2  py-[14px] px-[12px] rounded-2xl capitalize flex justify-between items-center">
              <span className="flex justify-center items-center gap-4">
                <Buld />
                <p className=" font-normal text-[1rem]">
                  Looking to hire?
                  <span className="text-[1rem] font-bold leading-3 ">
                    Switch to your Client profile.
                  </span>
                </p>
              </span>
              <span>
                <button onClick={handleQuict}>
                  <Quit />
                </button>
              </span>
            </div>
          ) : (
            <p></p>
          )}
          <Card />
        </div>
      </div>
    </div>
  );
};

export default Main;
