import { React, useState } from "react";
import { Link } from "react-router-dom";
import DownArrow from "../../Assets/Icons/DownArrow";
export default function Filter() {
  const [Role, setRole] = useState(false);
  const [Type, setType] = useState(false);
  const handleClick = () => {
    setRole(!Role);
  };
  return (
    <div className="flex min-w-[15rem] flex-col items-start justify-center  gap-7 bg-transparent ">
      <div className=" flex w-full items-center justify-between">
        <h1 className=" font-semibold">Filter(0)</h1>
        <div className="  !border-b-transprent flex rounded-sm border-[3px] border-solid  border-transparent text-lg font-semibold leading-[1.5] text-gray-400   ">
          <Link
            to="/"
            className=" top-[.18rem] after:block after:h-[2.5px] after:w-0 after:bg-yellow-300 after:transition-width  hover:after:w-[5.2rem] hover:after:duration-300  "
          >
            Clear Filters
          </Link>
        </div>
      </div>
      <div className=" flex w-full items-center justify-between">
        {" "}
        <h1 className=" font-semibold">Role</h1>
        <div
          onClick={handleClick}
          className=" absolute right-10 top-[43.7%] md:hidden"
        >
          <Link to="" className={`hamburger ${Role ? "active" : ""}`} href="#">
            <span></span>
          </Link>
        </div>
      </div>
      <div className=" flex w-full items-center justify-between">
        <h1 className=" font-semibold">Type</h1>
        <DownArrow />
      </div>
    </div>
  );
}
