import { React, useState } from "react";
import { Link } from "react-router-dom";
import DownArrow from "../../Assets/Icons/DownArrow";
export default function Filter() {
  const [Role, setRole] = useState(false);
  const [Type, setType] = useState(false);
  const handleClickRole = () => {
    setRole(!Role);
  };
  const handleClickType = () => {
    setType(!Type);
  };
  return (
    <div className="flex min-w-[15rem] flex-col items-start justify-center  gap-7 bg-transparent ">
      <div className=" flex w-full items-center justify-between">
        <h1 className=" font-semibold">Filter(0)</h1>
        <div className="  !border-b-transprent flex rounded-sm border-[3px] border-solid  border-transparent text-[1rem] font-semibold leading-[1.5] text-gray-400   ">
          <Link
            to="/"
            className=" top-[.18rem] after:block after:h-[2.5px] after:w-0 after:bg-yellow-300 after:transition-width  hover:after:w-[5.8rem] hover:after:duration-300  "
          >
            Clear Filters
          </Link>
        </div>
      </div>
      <div
        onClick={handleClickRole}
        className="  flex w-full items-center justify-between"
      >
        <h1 className=" font-semibold">Role</h1>
        <span>
          {Role ? (
            <div
              style={{
                transition: "transform .3s ease",
                transform: "rotate(180deg)",
              }}
            >
              <DownArrow />
            </div>
          ) : (
            <div
              style={{
                transform: "rotate(0deg)",
                transition: "transform .2s ease",
              }}
            >
              <DownArrow />
            </div>
          )}
        </span>
      </div>
      <div>
        {Role ? (
          <div className="flex flex-col items-start justify-start gap-2">
            <div className=" flex gap-2">
              <input type="checkbox" />
              <p className=" text-lg font-normal leading-[1.5] tracking-[1.3]">
                Design
              </p>
            </div>
            <div className=" flex gap-2">
              <input type="checkbox" />
              <p className=" leading-[1.5]">Engineering</p>
            </div>
            <div className=" flex gap-2">
              <input type="checkbox" />
              <p className=" leading-[1.5]">Social Media / Marketing</p>
            </div>
            <div className=" flex gap-2">
              <input type="checkbox" />
              <p className=" leading-[1.5]">Video & Animation</p>
            </div>
            <div className=" flex gap-2">
              <input type="checkbox" />
              <p className=" leading-[1.5]">Web 3</p>
            </div>
            <div className=" flex gap-2">
              <input type="checkbox" />
              <p className=" leading-[1.5]">Writing</p>
            </div>
          </div>
        ) : (
          ""
        )}
      </div>
      <div className=" flex w-full items-center justify-between">
        <h1 className=" font-semibold">Type</h1>
        <span onClick={handleClickType}>
          {Type ? (
            <div
              style={{
                transition: "transform .3s ease",
                transform: "rotate(180deg)",
              }}
            >
              <DownArrow />
            </div>
          ) : (
            <div
              style={{
                transform: "rotate(0deg)",
                transition: "transform .2s ease",
              }}
            >
              <DownArrow />
            </div>
          )}
        </span>
      </div>
    </div>
  );
}
